import React,{useState,useEffect} from 'react'
import {Link,useParams} from "react-router-dom";
import './User.css'
import site from "../../../assets/img.png";
import github from "../../../assets/img_1.png";
import location from "../../../assets/img_2.png";
import user from "../../../assets/img_3.png";
import user_2 from "../../../assets/img_4.png";
import axios from "../../../axios";
const  User = () => {
    const {login}=useParams();
    const [userInfo,setUserInfo]=useState({});
    const [repos,setRepos]=useState([]);

    useEffect(()=>{
        const fetchUserInformation=async ()=>{
            try{
                const response=await Promise.all([
                    axios.get(`/users/${login}`),
                    axios.get(`/users/${login}/repos`)
                ]);
                setUserInfo(response[0].data);
                setRepos(response[1].data);
                console.log(response);
                console.log(userInfo);
            }
            catch(error)
            {
                console.log(error);
            }



        };
        fetchUserInformation();
    },[]);
    return(
        <React.Fragment>
            <div className="container">
                <Link to="/" className="back">Back</Link>
               <div className="user-information">
                   <div className="image">
                       <img src={userInfo?.avatar_url} alt=""/>

                   </div>
                   <div className="user-content">
                       <h3>{userInfo?.name}</h3>
                       <p>{userInfo?.bio}</p>
                       <div className="more-data">
                           <p><img src={user}/>{userInfo?.followers} followers. Following {userInfo?.following}</p>
                           {userInfo.location && <p><img src={location}/>{userInfo?.location}</p>}
                           {userInfo?.blog && <p><img src={site}/>Portfolio.com</p>}
                           <p><img src={github}/><a href="#">View Git Hub Profile</a></p>

                       </div>
                   </div>
               </div>
                <div className="user-repos">
                    <div className="repo">
                        <h3>
                            <a href="#">Name of the Repo</a>
                        </h3>
                        <p>Lorem ipsum dolor sit amet, consectetur adipisicing elit. Numquam, ullam?</p>
                        <small>Written in JavaScript</small>

                    </div>
                    <div className="repo">
                        <h3>
                            <a href="#">Name of the Repo</a>
                        </h3>
                        <p>Lorem ipsum dolor sit amet, consectetur adipisicing elit. Numquam, ullam?</p>
                        <small>Written in JavaScript</small>

                    </div>
                    <div className="repo">
                        <h3>
                            <a href="#">Name of the Repo</a>
                        </h3>
                        <p>Lorem ipsum dolor sit amet, consectetur adipisicing elit. Numquam, ullam?</p>
                        <small>Written in JavaScript</small>

                    </div>
                    <div className="repo">
                        <h3>
                            <a href="#">Name of the Repo</a>
                        </h3>
                        <p>Lorem ipsum dolor sit amet, consectetur adipisicing elit. Numquam, ullam?</p>
                        <small>Written in JavaScript</small>

                    </div>
                </div>

            </div>


        </React.Fragment>

    );
};


export default User;
